arrayPipelines.data:
[
  {
    $match: {
      viewMode: false,
			isSubscribed: true,
			stripeCustomerId: '{{getSegmentSFAccounts.data.find(item => item.id === SFTableData.selectedRow.Id)?.stripe_id_c}}',
			$expr: {
        $eq: [{ $strLenCP: "$instanceUserId" }, 64]
      }
    }
  },
  {
    $group: {
      _id: "$instanceUserId"
      count: { $sum: 1 }
    }
  }, {
		$sort: {
			count: -1
		}
	}
]

